import React, { Component } from 'react';
import Layout from '../../../../layouts/mainLayout/MainLayout';
import Header from '../../../../../pageSettings/header/Header';
import style from './productsMOTHERBOARD.module.css';


export default class ProductsMOTHERBOARD extends Component {
  state = {
    products: [{ model: 'Loading data...' }],
    showData: false,
    selectedIndex: null,
  };

  async getProducts() {
    const response = await fetch('http://localhost:5000/getData/motherboard');
    const result = await response.json();
    console.log(result);
    this.setState({ products: result });
  }

  componentDidMount() {
    this.getProducts();
  }

  handleClick = index => {
    this.setState({
      showData: true,
      selectedIndex: index,
    });
  };

  render() {
    const { products, showData, selectedIndex } = this.state;

    return (
      <Layout>
        <Header h1Heading='Motherboard Products' />
        <section className={style.external}>
          <section className={style.model}>
            <h2>List Of Products</h2>
            {products.map((product, index) => (
              <section key={index}>

                <button></button>
                <Button
                  text={product.model}
                  type='button'
                  fun={() => {
                    this.handleClick(index);
                  }}
                  className={style.productButton}
                />
              </section>
            ))}
          </section>
          {showData && selectedIndex !== null && (
            <section className={style.showAlldata}>
              <h2>Product Data</h2>
              <div>
                <h3>Model: {products[selectedIndex].model}</h3>
                <h3>
                  CPU Socket Support:{' '}
                  {products[selectedIndex].cpu_socket_support}
                </h3>
                <h3>Chipset: {products[selectedIndex].chipset}</h3>
                <h3>Memory: {products[selectedIndex].memory}</h3>
                <h3>Form Factor: {products[selectedIndex].form_factor}</h3>
                <h3>
                  Product Expansion Slots:{' '}
                  {products[selectedIndex].expansion_slots}
                </h3>
                <h3>M2 Slot: {products[selectedIndex].M2Slot}</h3>
              </div>
            </section>
          )}
        </section>
      </Layout>
    );
  }
}
