import React, { Component } from 'react';
import PageLayout from '../../layouts/pageLayout/PageLayout';
import Header from '../../../commonComponents/header/Header';
import LinkLayout from '../../../commonComponents/linkLayout/LinkLayout';
import style from './questionAnswerUser.module.css';

/**
 * Description -
 * @returns
 */

export default class QuestionAnswerUser extends Component {
  state = {
    questionAnswerData: [],
    showData: false,
    selectIndex: null,
    userId: JSON.parse(localStorage.getItem('user')),
  };

  async getQuestionAnswer() {
    const response = await fetch(
      'http://localhost:5000/userQuestionAnswer/getData',
      {
        method: 'POST',
        crossDomain: true,
        headers: {
          'Content-Type': 'application/json',
          Accept: 'application/json',
          'Accept-Control-Allow-Origin': '*',
        },
        body: JSON.stringify({
          userId: this.state.userId._id,
        }),
      }
    );
    const result = await response.json();
    this.setState({ questionAnswerData: result });
  }

  componentDidMount() {
    this.getQuestionAnswer();
  }

  handelClick = index => {
    this.setState({
      showData: true,
      selectIndex: index,
    });
  };
  render() {
    return (
      <PageLayout>
        <Header h1Heading='Question/Answer' />
        <section className={style.container}>
          <LinkLayout toLink='/newQuestion' nameLink='New Question' />
          <LinkLayout
            toLink='/existsQuestionsUser'
            nameLink='Exists Questions'
          />
        </section>
      </PageLayout>
    );
  }
}
