import React, { Component } from 'react';
import PageLayout from '../../../layouts/pageLayout/PageLayout';
import Header from '../../../../pageSettings/header/Header';
import style from './existsQuestions.module.css';
import Button from '../../../../pageSettings/button/Button'

export default class ExistsQuestions extends Component {
  state = {
    products: [{ questionName: 'Loading data...' }],
    showData: false,
    selectIndex: null,
  };

  async getProducts() {
    const response = await fetch('http://localhost:5000/getData/cpu');
    const result = await response.json();
    console.log(result);
    this.setState({ products: result });
  }

  componentDidMount() {
    this.getProducts();
  }

  handelClick = index => {
    this.setState({
      showData: true,
      selectIndex: index,
    });
  };
  render() {
    return (
      <PageLayout>
        <Header h1Heading='Exists Questions' />
        <section className={style.external}>
          <section className={style.model}>
            <h2>List Of Products</h2>
            {this.state.products.map((product, index) => (
              <section key={index}>
                <button
                  onClick={() => {
                    this.handelClick(index);
                  }}
                  className={style.productButton}
                >
                  {product.questionName}
                </button>
              </section>
            ))}
          </section>
          {this.state.showData && this.state.selectIndex !== null && (
            <section className={style.showAllData}>
              <h2 className={style.h2}>Product Data</h2>
              <div>
                
                <h3>
                  Question Text:
                  {this.state.products[this.state.selectIndex].questionText}
                </h3>
                <Button text='Delete' />
                <section>
                  <form
                    onSubmit={this.handleSubmit.bind(this)}
                    className={style.form}
                  >
                    <input
                      type='text'
                      placeholder='Enter Question Name:'
                      value={this.state.questionName}
                      required
                      onChange={e =>
                        this.setState({ questionName: e.target.value })
                      }
                    />

                    <textarea
                      name='tarea'
                      rows='6'
                      cols='40'
                      placeholder=' Write text here'
                      required
                      onChange={e =>
                        this.setState({ questionText: e.target.value })
                      }
                    ></textarea>

                    <Button type='submit' text='submit' />
                    <p>{this.state.questionSend}</p>
                  </form>
                </section>
              </div>
            </section>
          )}
        </section>
      </PageLayout>
    );
  }
}
