import React, { Component } from 'react';
import Layout from '../../../../../layouts/mainLayout/MainLayout';
import Header from '../../../../../../pageSettings/header/Header';
import style from '../../../motherboard/products/productsMOTHERBOARD.module.css';

export default class ProductsCpuCoolerLiquid extends Component {
  state = {
    products: [{ model: 'Loading data...' }],
    showData: false,
    selectIndex: null,
  };

  async getProducts() {
    const response = await fetch(
      'http://loacalhost:5000/getData/cpuCoolerLiquid'
    );
    const result = await response.json();
    console.log(result);
    this.setState({ products: result });
  }

  componentDidMount() {
    this.getProducts();
  }

  handelClick = index => {
    this.setState({
      showData: true,
      selectIndex: index,
    });
  };
  render() {
    return (
      <Layout>
        <Header h1Heading='Cpu Cooler Liquid Products' />
        <section className={style.external}>
          <section className={style.model}>
            <h2>List Of Products</h2>
            {this.state.products.map((product, index) => (
              <section key={index}>
                <button
                  onClick={() => {
                    this.handleClick(index);
                  }}
                  className={style.productButton}
                >
                  {product.model}
                </button>
              </section>
            ))}
          </section>
          {this.state.showData && this.state.selectIndex !== null && (
            <section className={style.showAllData}>
              <h2 className={style.h2}>Product Data</h2>
              <div>
                <h3>
                  Model: {this.state.products[this.state.selectIndex].model}
                </h3>
                <h3>
                  Socket_Support:
                  {this.state.products[this.state.selectedIndex].socket_support}
                </h3>
                <h3>
                  Radiator Size:
                  {this.state.products[this.state.selectedIndex].radiator_size}
                </h3>
              </div>
            </section>
          )}
        </section>
      </Layout>
    );
  }
}
