import React from 'react'
import ComponentLearnLayout from '../../../../layouts/componentLearnLayout/ComponentLearnLayout';
import Header from '../../../../../commonComponents/header/Header';

/**
 * Description - This Function display the hard drive information
 * @returns - hard drive information
 */
function infoHD() {
  return (
    <ComponentLearnLayout name='hd'>
      <Header h1Heading='Hard Drive Information' />
      <h2>What is a hard disk drive (HDD)?</h2>
      <p>
        A computer hard drive (or a hard disk or HDD) is one kind of technology
        that stores the operating system, applications, and data files such a
        documents, pictures and music that your computer uses. The rest of the
        components in your computer work together to show you the applications
        and files stored on your hard drive.
      </p>
      h2
    </ComponentLearnLayout>
  );
}

export default infoHD
