import React, { Component } from 'react';
import Layout from '../../../layouts/mainLayout/MainLayout';
import Header from '../../../../pageSettings/header/Header';
import style from './mountedAssemblies.module.css';

export default class MountedAssemblies extends Component() {
  state = {
    assemblies: [{ name: 'Loading data...' }],
    showData: false,
    selectIndex: null,
  };

  async getAssembly() {
    const response = await fetch('http://localhost:5000/getAssembly');
    const result = await response.json();
    console.log(result);
    this.setState({ assemblies: result });
  }

  componentDidMount() {
    this.getAssembly();
  }
  handelClick = index => {
    this.setState({
      showData: true,
      selectIndex: index,
    });
  };

  render() {
    return (
      <Layout>
        <Header h1Heading='Mounted Assemblies' />

        <section className={style.external}>
          <section className={style.model}>
            <h2>List Of Assemblies</h2>
            {this.state.assemblies.map((assembly, index) => (
              <section key={index}>
                <button
                  onClick={() => {
                    this.handelClick(index);
                  }}
                  className={style.assemblyButton}
                >
                  {assembly.model}
                </button>
              </section>
            ))}
          </section>
          {this.state.showData && this.state.selectIndex !== null && (
            <section className={style.showAllData}>
              <h2 className={style.h2}>Assembly Data</h2>
              <div>
                <h3>
                  Case: {this.state.products[this.state.selectIndex].model}
                </h3>
                <h3>
                  Motherboard: {this.state.products[this.state.selectIndex].form}
                </h3>
                <h3>
                  Radiator Compatibility:{' '}
                  {
                    this.state.products[this.state.selectIndex]
                      .radiator_compatibility
                  }
                </h3>
                <h3>
                  Dimensions:{' '}
                  {this.state.products[this.state.selectIndex].dimensions}
                </h3>
              </div>
            </section>
          )}
        </section>
      </Layout>
    );
  }
}
