import React, { Component } from 'react';
import PageLayout from '../../layouts/pageLayout/PageLayout';
import Header from '../../../commonComponents/header/Header';
import style from './questionAnswerPremium.module.css';
import Button from '../../../commonComponents/button/Button';

/**
 * Description -
 */
export default class QuestionAnswerPremium extends Component {
  state = {
    questionAnswerData: [],
    questions: [],
    users: [],
    showData: false,
    selectIndex: null,
    showQuestionsNames: false,
    questionIndex: null,
    answerText: '',
    answerText2: '',
  };

  async getQuestionAnswer() {
    const response = await fetch(
      'http://localhost:5000/premiumQuestionAnswer/getData',
      {
        method: 'POST',
        crossDomain: true,
        headers: {
          'Content-Type': 'application/json',
          Accept: 'application/json',
          'Accept-Control-Allow-Origin': '*',
        },
      }
    );
    const result = await response.json();
    this.setState({ questionAnswerData: result });
    for (let user of result) {
      if (!this.state.users.includes(user.userEmail)) {
        this.state.users.push(user.userEmail);
      }
    }
  }

  componentDidMount() {
    this.getQuestionAnswer();
  }

  handelClick = index => {
    if (index >= this.state.questions.length) {
      this.setState({
        showData: true,
        selectIndex: 0,
      });
    } else {
      this.setState({
        showData: true,
        selectIndex: index,
      });
    }
  };

  handelClickQuestion = qA => {
    this.setState({ questions: [] });
    if (this.state.selectIndex) this.setState({ selectIndex: 0 });
    let array = [];
    for (let data of this.state.questionAnswerData) {
      if (data.userEmail === qA) {
        array.push(data);
        this.setState(prevState => ({
          showQuestionsNames: true,
        }));
      }
    }
    this.setState({ questions: array });
  };

  /**
   * Description - This function update the question text in the server
   * @param {*} e - Question text value
   */
  async handleSubmit(e) {
    e.preventDefault();
    let dataToSend;
    if (!this.state.questionAnswerData[this.state.selectIndex].haveAnAnswer) {
      dataToSend = {
        userId: this.state.userId,
        answerText: this.state.answerText,
        questionName:
          this.state.questionAnswerData[this.state.selectIndex].questionName,
      };
    } else {
      dataToSend = {
        userId: this.state.userId,
        answerText:
          this.state.questionAnswerData[this.state.selectIndex].answerText +
          ' | ' +
          this.state.answerText,
        questionName:
          this.state.questionAnswerData[this.state.selectIndex].questionName,
      };
    }

    const questionInputData = {
      method: 'POST',
      crossDomain: true,
      headers: {
        'Content-Type': 'application/json',
        Accept: 'application/json',
        'Accept-Control-Allow-Origin': '*',
      },
      body: JSON.stringify(dataToSend),
    };

    const response = await fetch(
      'http://localhost:5000/userQuestionAnswer/updateAnswer',
      questionInputData
    );
    const result = await response.json();
    // console.log(result);
    if (result.status === 'true') {
      this.setState({
        showResult: 'The Answer has been updated',
      });
      this.setState({
        questionText2:
          this.state.questionAnswerData[this.state.selectIndex].questionText +
          ' ? ' +
          this.state.questionText,
      });

      setTimeout(() => {
        this.setState({
          showResult: '',
        });
      }, 1000);
    }
  }
  render() {
    const { showData, selectIndex, showQuestionsNames } = this.state;
    return (
      <PageLayout>
        <Header h1Heading='Question Answer' />
        <section className={style.external}>
          <section className={style.model}>
            <h2>List Of Questions/Answers</h2>
            {this.state.users.map((qA, index) => (
              <section key={index}>
                <button
                  onClick={() => {
                    this.handelClickQuestion(qA);
                  }}
                  className={style.productButton}
                >
                  {qA}
                </button>
              </section>
            ))}

            {showQuestionsNames ? (
              <section className={style.questions}>
                {this.state.questions.map((qA, index) => {
                  console.log(qA);
                  return (
                    <section key={qA._id}>
                      <button
                        className={style.questionButton}
                        onClick={() => {
                          this.handelClick(index);
                        }}
                      >
                        {qA.questionName}
                      </button>
                    </section>
                  );
                })}
              </section>
            ) : (
              ''
            )}
          </section>

          {showData &&
            selectIndex !== null &&
            selectIndex < this.state.questions.length && (
              <section className={style.showAllData}>
                <h2 className={style.h2}>Question/Answer Data</h2>
                <section>
                  <h3 className={style.h3}>
                    <span className={style.span}>Question Name:</span>

                    {this.state.questions[selectIndex].questionName}
                  </h3>
                  <h3 className={style.h3}>
                    <span className={style.span}> Question Text:</span>

                    {this.state.questions[selectIndex].questionText}
                  </h3>

                  <h3 className={style.h3}>
                    <span className={style.span}> Question Answer:</span>

                    {this.state.questions[selectIndex].answerText}
                  </h3>

                  <section>
                    <form
                      onSubmit={this.handleSubmit.bind(this)}
                      className={style.form}
                    >
                      <textarea
                        className={style.textArea}
                        value={this.state.questionText}
                        name='tarea'
                        rows='6'
                        cols='40'
                        placeholder=' Write text here'
                        required
                        onChange={e =>
                          this.setState({ questionText: e.target.value })
                        }
                      ></textarea>

                      <Button type='submit' text='submit' />

                      {/* {questionAnswerData[selectIndex].haveAnAnswer && (
                        <section className={style.starsLocation}>
                          {[1, 2, 3, 4, 5].map(star => (
                            <span
                              key={star}
                              style={{
                                cursor: 'pointer',
                                color:
                                  star <= selectedStars ? 'orange' : 'grey',
                                fontSize: `60px`, // Set the font size to control the star size
                              }}
                              onClick={() => this.handleStarClick(star)}
                            >
                              ★
                            </span>
                          ))}
                        </section>
                      )} */}
                    </form>
                  </section>
                </section>
              </section>
            )}
        </section>
      </PageLayout>
    );
  }
}
