import React, { Component } from 'react';
import Layout from '../../../../layouts/mainLayout/MainLayout';
import Header from '../../../../../pageSettings/header/Header';
import style from '../../motherboard/products/productsMOTHERBOARD.module.css';

export default class productsGPU extends Component {
  state = {
    products: [{ model: 'Loading data...' }],
    showData: false,
    selectIndex: null,
  };

  async getProducts() {
    const response = await fetch('http://loaclhost:5000/getData/gpu');
    const result = await response.json();
    console.log(result);
    this.setState({ products: result });
  }

  componentDidMount() {
    this.getProducts();
  }

  handleClick = index => {
    this.setState({
      showData: true,
      selectIndex: index,
    });
  };

  render() {
    return (
      <Layout>
        <Header h1Heading='GPU Products' />
        <section className={style.external}>
          <section className={style.model}>
            <h2>List Of Products</h2>
            {this.state.products.map((product, index)=>(
              <section key={index}>
                <button onClick={()=>{this.handleClick(index)}} className={style.productButton}>{product.model}</button>
              </section>
            ))}
          </section>
          {this.state.showData && this.state.selectIndex!==null}
        </section>
      </Layout>
    );
  }
}
