import React, { Component } from 'react';
import style from './ForgetPassword.module.css';
import { Link } from 'react-router-dom';
import Button from '../../pageSettings/button/Button';

export default class ResetPassword extends Component {
  state = {
    email: '',
    showResult: '',
  };
  handleSubmit(e) {
    // Preventing the default form submission behavior
    e.preventDefault();
    const { email } = this.state;

    fetch('http://localhost:5000/forgot-password', {
      method: 'POST',
      crossDomain: true,
      headers: {
        'Content-Type': 'application/json',
        Accept: 'application/json',
        'Access-Control-Allow-Origin': '*',
      },
      body: JSON.stringify({
        email,
      }),
    })
      .then(res => res.json())
      .then(data => {
        if (data.result === 'User Not Exist')
          this.setState({
            showResult: 'User Not Exists!! Check your details',
          });
        else if (data.result === 'Error email not send')
          this.setState({
            showResult: 'Error email not send',
          });
        else if (data.result === 'Email send')
          this.setState({
            showResult: 'Email sended, Check your email',
          });
      });
  }
  render() {
    return (
      <form className={style.form} onSubmit={this.handleSubmit.bind(this)}>
        <h3>Forget Password</h3>
        <div>
          <label>Email address</label>
          <input
            type='email'
            placeholder='Enter email'
            onChange={e => this.setState({ email: e.target.value })}
            required
          />
        </div>
        <div className={style.div}>
          <Button type='submit' className={style.submit} text='submit' />
        </div>
        <p>{this.state.showResult}</p>
        <p>
          <Link to='/'>Sign In</Link>
        </p>
      </form>
    );
  }
}
