import React, { Component } from 'react';
import Layout from '../../../../layouts/mainLayout/MainLayout';
import Header from '../../../../../pageSettings/header/Header';
import style from './productsMOTHERBOARD.module.css';
import Button from '../../../../../pageSettings/button/Button';

export default class ProductsMOTHERBOARD extends Component {
  state = {
    products: [{ model: 'Loading data...' }],
    showData: false,
  };

  async getProducts() {
    const response = await fetch('http://localhost:5000/getData/motherboard');
    const result = await response.json();
    console.log(result);
    this.setState({ products: result });
  }

  componentDidMount() {
    this.getProducts();
  }

  handleClick = () => {
    this.setState({
      showData: !this.state.showData,
    });
  };

  render() {
    return (
      <Layout>
        <Header h1Heading='Motherboard Products' />
        <section className={style.external}>
          {this.state.products.map((product, index) => {
            return (
              <section key={index} className={style.externalSection}>
                <h2>List Of Products</h2>
                <Button
                  text={product.model}
                  type='button'
                  fun={() => {
                    this.handleClick();
                  }}
                />
              </section>
            );
          })}
          {this.state.showData && (
            <section className={style.showAlldata}>
              <h2>Product Data</h2>
              {this.state.products.map((product, index) => {
                return (
                  <div key={index}>
                    <h3>Model: {product.model}</h3>
                    <h3>CPU Socket Support: {product.cpu_socket_support}</h3>
                    <h3>Chip set: {product.chipset}</h3>
                    <h3>Memory: {product.memory}</h3>
                    <h3>Form Factor: {product.form_factor}</h3>
                    <h3>Product Expansion Slots: {product.expansion_slots}</h3>
                    <h3>M2 Slot: {product.M2Slot}</h3>
                  </div>
                );
              })}
            </section>
          )}
        </section>
      </Layout>
    );
  }
}
