import React, { Component } from 'react';
import Header from '../../../commonComponents/header/Header';
import style from './premiumHome.module.css';
import PageLayout from '../../layouts/pageLayout/PageLayout';
import DiagnosticInformationLayout from '../../layouts/diagnosticInformationLayout/DiagnosticInformationLayout';

/**
 * Description -
 */
export default class PremiumHome extends Component {
  state = {
    usersData: [],
    amountOfUsers: 0,
    questionAnswerData: [],
    openQuestions: 0,
    closedQuestions: 0,
    overallUsersRate: 0,
    mostLearnedComp: '',
    learnedData: [],
  };
  render() {
    return (
      <PageLayout>
        <Header h1Heading='PC BUILDER Premium' />
        <div className={style.adminHome}>
          <section className={style.section}>
            <DiagnosticInformationLayout
              h2='Open Questions'
              text={this.state.openQuestions}
              to='/questionsAnswersReport'
              name='More Info'
            />
            <DiagnosticInformationLayout
              h2='Close Questions'
              text={this.state.closedQuestions}
              to='/questionsAnswersReport'
              name='More Info'
            />

            <DiagnosticInformationLayout
              h2='Overall Users Rate'
              text={Array.from(
                { length: this.state.overallUsersRate },
                (_, index) => (
                  <span key={index}>â˜…</span>
                )
              )}
              to='/questionsAnswersReport'
              name='More Info'
            />

            <DiagnosticInformationLayout
              h2='Amount Of Users'
              text={this.state.amountOfUsers}
              to='/usersDataReport'
              name='More Info'
            />

            <DiagnosticInformationLayout
              h2='Most Learned Component '
              text={this.state.mostLearnedComp}
              to='/learnedDataReport'
              name='More Info'
            />
          </section>
        </div>
      </PageLayout>
    );
  }
}
