import React, { Component } from 'react';
import Layout from '../../../../layouts/mainLayout/MainLayout';
import Header from '../../../../../pageSettings/header/Header';
import style from '../../motherboard/products/productsMOTHERBOARD.module.css';

export default class ProductsCASE extends Component {
  state = {
    products: [{ model: 'Loading data...' }],
    showData: false,
    selectedIndex: null,
  };

  async getProducts() {
    const response = await fetch('http://localhost:5000/getData/case');
    const result = await response.json();
    console.log(result);
    this.setState({ products: result });
  }

  componentDidMount() {
    this.getProducts();
  }

  handelClick = index => {
    this.setState({
      showData: true,
      selectedIndex: index,
    });
  };
  render() {
    return (
      <Layout>
        <Header h1Heading='Case Products' />
        <section className={style.external}>
          <section className={style.model}>
            <h2>List Of Products</h2>
            {this.state.products.map((product, index) => (
              <section key={index}>
                <button
                  onClick={() => {
                    this.handelClick(index);
                  }}
                  className={style.productButton}
                >
                  {product.model}
                </button>
              </section>
            ))}
          </section>
          {this.state.showData && this.state.selectedIndex !== null && (
            <section className={style.showAllData}>
              <h2 className={style.h2}>Product Data</h2>
              <div>
                <h3>
                  Model: {this.state.products[this.state.selectedIndex].model}
                </h3>
                <h3>
                  Form: {this.state.products[this.state.selectedIndex].form}
                </h3>
                <h3>
                  Radiator Compatibility:{' '}
                  {
                    this.state.products[this.state.selectedIndex]
                      .radiator_compatibility
                  }
                </h3>
                <h3>
                  Dimensions:{' '}
                  {this.state.products[this.state.selectedIndex].dimensions}
                </h3>
              </div>
            </section>
          )}
        </section>
      </Layout>
    );
  }
}
