import React, { Component } from 'react';
import style from './LearnLayout.module.css';
import { Link } from 'react-router-dom';

export default class LearnLayout extends Component {
  /**
   * Description - For every push on the button of learn/not learned the data save in databse
   * @param {*} buttonId 
   */
  updateButton(buttonId) {
    let user = JSON.parse(localStorage.getItem('user'));
    let allCompLearned = this.props.databaseArray;
    // console.log(allCompLearned);
    if (allCompLearned[buttonId].haveLearned) {
      allCompLearned[buttonId].haveLearned = false;
    } else {
      allCompLearned[buttonId].haveLearned = true;
    }
    this.props.changeLearn({ ...allCompLearned });

    fetch('http://localhost:5000/compLearned', {
      method: 'POST',
      crossDomain: true,
      headers: {
        'Content-Type': 'application/json',
        Accept: 'application/json',
        'Access-Control-Allow-Origin': '*',
      },
      body: JSON.stringify({
        allCompLearnedServer: allCompLearned,
        userIdServer: user._id,
      }),
    })
      .then(res => res.json())
      .then(data => {
        console.log(data);
      });
  }

  render() {
    const image = this.props.img;
    const imageName = this.props.imgName;
    const compName = this.props.name;
    const compInfo = this.props.compInfo;
    const compProduct = this.props.compProduct;
    return (
      <div className={style.box}>
        <img
          src={image}
          alt={imageName}
          width='227'
          height='180'
          className={style.img}
        />
        <div
          className={`${style.text} ${
            this.props.learn && this.props.learn.haveLearned === true
              ? style.textWithotOpacity
              : ''
          }`}
        >
          <div>
            <b>{compName}</b>
          </div>
          <Link className={style.link} to={compInfo}>
            Information
          </Link>
          <Link className={style.link} to={compProduct}>
            Products
          </Link>
          {this.props.learn ? (
            <div>
              {this.props.learn.haveLearned ? (
                <button
                  className={`${style.but} ${
                    this.props.learn.haveLearned === true
                      ? style.green
                      : style.red
                  }`}
                  onClick={() => this.updateButton(imageName)}
                >
                  Learned
                </button>
              ) : (
                <button
                  className={`${style.but} ${
                    this.props.learn.haveLearned === true
                      ? style.green
                      : style.red
                  }`}
                  onClick={() => this.updateButton(imageName)}
                >
                  Not learned
                </button>
              )}
              <p className={style.pComment}>{this.props.learn.comment}</p>
            </div>
          ) : (
            <></>
          )}
        </div>
      </div>
    );
  }
}
