import React, { Component } from 'react';
import PageLayout from '../../../../userPages/layouts/pageLayout/PageLayout';
import Header from '../../../../pageSettings/header/Header';
import style from './delteAssemlies.module.css'

export default class DeleteAssemblies extends Component {
  state = {
    assembly: [{ assemblyName: 'Loading data...' }],
  };

  async getAssembly() {
    const response = await fetch(
      'http://localhost:5000/getAssemblies/assemblies'
    );
    const result = await response.json();
    console.log(result);
    this.setState({ assembly: result });
  }

  componentDidMount() {
    this.getAssembly();
  }
  handelClick = index => {
    this.setState({
      showData: true,
      selectIndex: index,
    });
  };

  render() {
    return (
      <PageLayout>
        <Header h1Heading='Mounted Assemblies' />

        <select
          value={this.state.modelMotherboard}
          label='MOTHERBOARD'
          onChange={e => this.setState({ modelMotherboard: e.target.value })}
          required
        >
          <option value='' disabled>
            Select a Motherboard
          </option>
          {this.state.motherboardArray.map(itemMotherboard => {
            return (
              <option key={itemMotherboard} value={itemMotherboard}>
                {itemMotherboard}
              </option>
            );
          })}
        </select>

        
      </PageLayout>
    );
  }
}
