import React, { Component } from 'react';
import PageLayout from '../../../layouts/pageLayout/PageLayout';
import Header from '../../../../commonComponents/header/Header';
import style from './existsTests.module.css';

/**
 * Description - 
 */
export default class ExistsTests extends Component {
  state = {
    tests: [{ model: 'Loading data...' }],
    showData: false,
    selectIndex: null,
  };

  /**
   * Description
   */
  async getProducts() {
    const response = await fetch('http://localhost:5000/existsTests');
    const result = await response.json();
    console.log(result);
    this.setState({ products: result });
  }

  /**
   * Description
   */
  componentDidMount() {
    this.getProducts();
  }

  /**
   * Description
   * @param {*} index 
   */
  handelClick = index => {
    this.setState({
      showData: true,
      selectIndex: index,
    });
  };
  render() {
    return (
      <PageLayout>
        <Header h1Heading='Exists Test' />
        <section className={style.external}>
          <section className={style.model}>
            <h2>List Of Tests</h2>
            {this.state.tests.map((product, index) => (
              <section key={index}>
                <button
                  onClick={() => {
                    this.handelClick(index);
                  }}
                  className={style.productButton}
                >
                  {product.model}
                </button>
              </section>
            ))}
          </section>
          {this.state.showData && this.state.selectIndex !== null && (
            <section className={style.showAllData}>
              <h2 className={style.h2}>Product Data</h2>
              <div>
                <h3>
                  Model: {this.state.tests[this.state.selectIndex].model}
                </h3>
                <h3>
                  Fan Size:
                  {this.state.tests[this.state.selectIndex].fan_size}
                </h3>
              </div>
            </section>
          )}
        </section>
      </PageLayout>
    );
  }
}
