import React, { Component } from 'react';
import Layout from '../../../../layouts/mainLayout/MainLayout';
import Header from '../../../../../pageSettings/header/Header';
import style from '../../motherboard/products/productsMOTHERBOARD.module.css';

export default class productsGPU extends Component {
  state = {
    products: [{ model: 'Loading data...' }],
    showData: false,
    selectIndex: null,
  };

  async getProducts() {
    const response = await fetch('http://loaclhost:5000/getData/gpu');
    const result = await response.json();
    console.log(result);
    this.setState({ products: result });
  }

  componentDidMount() {
    this.getProducts();
  }

  handleClick = index => {
    this.setState({
      showData: true,
      selectIndex: index,
    });
  };

  render() {
    return (
      <Layout>
        <Header h1Heading='GPU Products' />
        <section className={style.external}>
          <section className={style.model}>
            <h2>List Of Products</h2>
            {this.state.products.map((product, index) => (
              <section key={index}>
                <button
                  onClick={() => {
                    this.handleClick(index);
                  }}
                  className={style.productButton}
                >
                  {product.model}
                </button>
              </section>
            ))}
          </section>
          {this.state.showData && this.state.selectIndex !== null && (
            <section className={style.showAllData}>
              <h2 className={style.h2}>Product Data</h2>
              <div>
                <h3>
                  Model: {this.state.products[this.state.selectIndex].model}
                </h3>
                <h3>
                  Bus:
                  {
                    this.state.products[this.state.selectedIndex]
                      .bus
                  }
                </h3>
                <h3>
                  Memory:{' '}
                  {this.state.products[this.state.selectedIndex].memory}
                </h3>
                <h3>
                  Engine Clock: {this.state.products[this.state.selectedIndex].engine_clock}
                </h3>
                <h3>
                  :{' '}
                  {this.state.products[this.state.selectedIndex].form_factor}
                </h3>
                <h3>
                  Product Expansion Slots:{' '}
                  {
                    this.state.products[this.state.selectedIndex]
                      .expansion_slots
                  }
                </h3>
                <h3>
                  M2 Slot:{' '}
                  {this.state.products[this.state.selectedIndex].M2Slot}
                </h3>
              </div>
            </section>
          )}
        </section>
      </Layout>
    );
  }
}
