import React, { Component } from 'react';
import Header from '../../../commonComponents/header/Header';
import style from './adminHome.module.css';
import DiagnosticInformationLayout from '../../layouts/diagnosticInformationLayout/DiagnosticInformationLayout';
import PageLayout from '../../layouts/pageLayout/PageLayout';

/**
 * Description This class show all Admin home data
 */
export default class AdminHome extends Component {
  state = {
    amountOfUsers: 0,
    amountOfOpenQuestions: 0,
    amountOfClosedQuestions: 0,
    overallUserSatisfaction: 0,
    professionalData: 'professional data',
    //
    questionAnswerData: [],
    openQuestions: 0,
    closedQuestions: 0,
  };

  /**
   * Description - This function get all the question/answer from the server
   */
  async getQuestionAnswer() {
    const response = await fetch(
      'http://localhost:5000/adminReports/getQuestionAnswerData',
      {
        method: 'POST',
        crossDomain: true,
        headers: {
          'Content-Type': 'application/json',
          Accept: 'application/json',
          'Accept-Control-Allow-Origin': '*',
        },
      }
    );
    const result = await response.json();

    this.setState(
      {
        questionAnswerData: result,
      },
      
    );
  }

  showQuestionAnswerData() {
    // console.log(this.state.questionAnswerData);
    for (let index = 0; index < this.state.questionAnswerData.length; index++) {
      console.log(index);
      if (!this.state.questionAnswerData[index].haveAnAnswer) {
        this.setState({ openQuestions: this.state.openQuestions + 1 });
      } else {
        this.setState({ closedQuestions: this.state.closedQuestions + 1 });
      }
    }
  }

  componentDidMount() {
    this.getQuestionAnswer();
  }
  render() {
    return (
      <PageLayout>
        <Header h1Heading='PC BUILDER ADMIN' />
        <div className={style.adminHome}>
          <section className={style.section}>
            <DiagnosticInformationLayout
              h2='Open Questions'
              text={this.state.openQuestions}
              to=''
              name='More Info'
            />
            <DiagnosticInformationLayout
              h2='Close Questions'
              text={this.state.closedQuestions}
              to=''
              name='More Info'
            />
            <DiagnosticInformationLayout
              h2='Amount of users'
              text={this.state.amountOfUsers}
              to=''
              name='More Info'
            />

            <DiagnosticInformationLayout
              h2='Professional Data'
              text={this.state.professionalData}
              to=''
              name='More Info'
            />
            <DiagnosticInformationLayout
              h2='Overall user satisfaction'
              text='*****'
              to=''
              name='More Info'
            />
          </section>
        </div>
      </PageLayout>
    );
  }
}
