import React, { Component } from 'react';
import PageLayout from '../../layouts/pageLayout/PageLayout';
import Header from '../../../commonComponents/header/Header';
import style from './questionAnswerPremium.module.css';
import Button from '../../../commonComponents/button/Button';

/**
 * Description -
 */
export default class QuestionAnswerPremium extends Component {
  state = {
    questionAnswerData: [],
    showData: false,
    selectIndex: null,
    showQuestionsNames: false,
    questionIndex: null,
  };

  async getQuestionAnswer() {
    const response = await fetch(
      'http://localhost:5000/premiumQuestionAnswer/getData',
      {
        method: 'POST',
        crossDomain: true,
        headers: {
          'Content-Type': 'application/json',
          Accept: 'application/json',
          'Accept-Control-Allow-Origin': '*',
        },
      }
    );
    const result = await response.json();
    this.setState({ questionAnswerData: result });
  }

  componentDidMount() {
    this.getQuestionAnswer();
  }

  handelClick = index => {
    this.setState({
      showData: true,
      selectIndex: index,
    });
  };

  handelClickQuestion = index => {
    this.setState(prevState => ({
      showQuestionsNames: !prevState.showQuestionsNames,
      questionIndex: index,
    }));

    console.log(this.state.questionAnswerData);
  };
  render() {
    const { questionAnswerData, showData,selectIndex,questionIndex, showQuestionsNames } =
      this.state;
    return (
      <PageLayout>
        <Header h1Heading='Question Answer' />
        <section className={style.external}>
          <section className={style.model}>
            <h2>List Of Questions/Answers</h2>
            {questionAnswerData.map((qA, index) => (
              <section key={index}>
                <button
                  onClick={() => {
                    this.handelClickQuestion(index);
                  }}
                  className={style.productButton}
                >
                  {qA.userEmail}
                </button>
              </section>
            ))}
            {showQuestionsNames &&
              questionIndex !== null &&
              questionIndex < questionAnswerData.length && (
                <section className={style.showAllData}>
                  <button
                    className={style.h2}
                    onClick={() => {
                      this.handelClick(q);
                    }}
                  >
                    {questionAnswerData[questionIndex].questionName}
                  </button>
                </section>
              )}
          </section>
          {showData &&
            selectIndex !== null &&
            selectIndex < questionAnswerData.length && (
              <section className={style.showAllData}>
                <h2 className={style.h2}>Question/Answer Data</h2>
                <div>
                  <h3>
                    Question Name:
                    {questionAnswerData[selectIndex].questionName}
                  </h3>
                  <h3>
                    Question Text:
                    {questionAnswerData[selectIndex].questionText}
                  </h3>
                  {!questionAnswerData[selectIndex].haveAnAnswer && (
                    <button
                      onClick={() => {
                        const indexToDelete =
                          questionAnswerData[selectIndex].questionName;

                        this.deleteQuestion(indexToDelete);
                      }}
                      className={style.deleteButton}
                    >
                      Delete
                    </button>
                  )}

                  <p className={style.showResult}>{this.state.showResult}</p>

                  <h3>
                    Question Answer:
                    {questionAnswerData[selectIndex].answerText}
                  </h3>

                  <section>
                    <form
                      onSubmit={this.handleSubmit.bind(this)}
                      className={style.form}
                    >
                      <textarea
                        className={style.textArea}
                        value={this.state.questionText}
                        name='tarea'
                        rows='6'
                        cols='40'
                        placeholder=' Write text here'
                        required
                        onChange={e =>
                          this.setState({ questionText: e.target.value })
                        }
                      ></textarea>

                      <Button type='submit' text='submit' />

                      {/* {questionAnswerData[selectIndex].haveAnAnswer && (
                        <section className={style.starsLocation}>
                          {[1, 2, 3, 4, 5].map(star => (
                            <span
                              key={star}
                              style={{
                                cursor: 'pointer',
                                color:
                                  star <= selectedStars ? 'orange' : 'grey',
                                fontSize: `60px`, // Set the font size to control the star size
                              }}
                              onClick={() => this.handleStarClick(star)}
                            >
                              â˜…
                            </span>
                          ))}
                        </section>
                      )} */}
                    </form>
                  </section>
                </div>
              </section>
            )}
        </section>
      </PageLayout>
    );
  }
}
