import React, { Component } from 'react';
import PageLayout from '../../../../userPages/layouts/pageLayout/PageLayout';
import Header from '../../../../pageSettings/header/Header';

export default class DeleteAssemblies extends Component {
  state = {
    assembly: [{ assemblyName: 'Loading data...' }],
  };

  async getAssembly() {
    const response = await fetch(
      'http://localhost:5000/getAssemblies/assemblies'
    );
    const result = await response.json();
    console.log(result);
    this.setState({ assembly: result });
  }

  componentDidMount() {
    this.getAssembly();
  }
  handelClick = index => {
    this.setState({
      showData: true,
      selectIndex: index,
    });
  };

  render() {
    return (
      <PageLayout>
        <Header h1Heading='Mounted Assemblies' />

        <section className={style.model}>
          <h2>List Of Assemblies</h2>
          {this.state.assembly.map((comp, index) => (
            <section key={index}>
              <button
                onClick={() => {
                  this.handelClick(index);
                }}
                className={style.assemblyButton}
              >
                {comp.assemblyName}
              </button>
            </section>
          ))}
        </section>
      </PageLayout>
    );
  }
}
